$(document).ready(function(){if(top.SwitchPwd){$("#password_field").hide();showInternalSwitch()}else{$("#password_field").show()}translate();bindBtnEvent();bindToolTip();parent.autoHeight()});function translate(){$("#telnetStartBtn").text(top.T.BTN.BusinessStart);$("#telnetSwitch").text(top.T.Switch.TelnetSwitch);$("#PasswordValidation").text(top.T.FileManagementPage.PasswordValidation);$("#InputPassword").text(top.T.FileManagementPage.InputPassword)}function bindBtnEvent(){$(".btn-submit").click(function(a){a.preventDefault();validatePwd();parent.autoHeight()});$(".btn-minimize").click(function(b){b.preventDefault();var a=$(this).parent().parent().next(".box-content");if(a.is(":visible")){$("i",$(this)).removeClass("chevron-up").addClass("chevron-down");$(this).removeClass("btn-minimize").addClass("btn-maximize")}else{$("i",$(this)).removeClass("chevron-down").addClass("chevron-up");$(this).removeClass("btn-maximize").addClass("btn-minimize")}bindToolTip();a.slideToggle();parent.autoHeight()})}function bindToolTip(){$(".btn-submit").attr("data-original-title",top.T.BTN.Submit);$(".btn-minimize").attr("data-original-title",top.T.BTN.Minimize);$(".btn-maximize").attr("data-original-title",top.T.BTN.Maximize);$('[rel="tooltip"],[data-rel="tooltip"]').tooltip({"placement":"bottom",delay:{show:400,hide:200}})}function showInternalSwitch(){var a=[];a.push('<div class="box span6">');a.push('	<div class="box-header">');a.push('		<h2><i class="halflings-icon align-justify"></i><span class="break"></span><span id="telnetSwitch"></span></h2>');a.push('		<div class="box-icon">');a.push('			<a href="#" class="btn-minimize" data-rel="tooltip" data-original-title=""><i class="halflings-icon chevron-up"></i></a>');a.push("		</div>");a.push("	</div>");a.push('	<div class="box-content">');a.push('		<div class="control-group">');a.push('			<div class="controls">');a.push('				<button class="btn btn-primary noty" onclick="telnetStart()" style="margin-left: 45%;">');a.push('					<i class="halflings-icon white bell"></i><span id="telnetStartBtn"></span>');a.push("				</button>");a.push("			</div>");a.push("		</div>");a.push("	</div>");a.push("</div>");$("#switch_one").empty().append(a.join(""))}function telnetStart(){var a="startTelnet;";parent.LMT.cmdSyn(a,function(b){if(parent.LMT.isCmdSuccess(b)){parent.ModalCommon.open(top.T.Main.Notice,top.T.AlertInfo.OperationSuccess,"ok")}else{parent.ModalCommon.open(top.T.Main.Notice,top.T.AlertInfo.OperationFail,"error")}})}function validatePwd(){var b=$("#password").val();if(b==""){top.ModalCommon.open(top.T.Main.Notice,top.T.FileManagementPage.PasswordError,"error");return}b=md5(b);var a='ValidatePwd : password = "'+b+'"';parent.LMT.cmdSyn(a,function(d){var c=parent.LMT.parser(d);if(c.result==="000"){$("#password_field").hide();showInternalSwitch();translate();top.SwitchPwd=true}else{top.ModalCommon.open(top.T.Main.Notice,top.T.FileManagementPage.PasswordError,"error")}})};